;; <=

;; **
;;; and plot histograms of the spread of connected bits.
;; **

;; @@
(defn potential-overlaps [columns input-bits on-bits dendrite-size 
                          & {:keys [plot-range bins] :or {plot-range [[0 on-bits] [0 450]] bins on-bits}}]
  (let [odds-bit-in-dendrite (/ dendrite-size input-bits)
        layer-data (run-trials columns on-bits odds-bit-in-dendrite)]
    (plot/histogram layer-data :plot-range plot-range :bins bins)))

;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;sdr-numbers/potential-overlaps</span>"}
;; <=

;; **
;;; Here are the plots for a 512-bit, 32-on input, 128 bit dendrites:
;; **

;; @@
(apply plot/compose (map #(potential-overlaps % 512 32 128) [128 512 1024 2048]))

